#include <bits/stdc++.h>

using namespace std;

int minimumDeletions(string s)
{
    int n = s.length();
    vector<char> st; // vector as stack ;
    int operation = 0;
    st.push_back(s[0]);
    for (int i = 1; i < n; i++)
    {
        if (!st.empty() && (st.back() == 'b' && s[i] == 'a'))
        {
            st.pop_back();
            operation += 1;
        }
        else
        {
            st.push_back(s[i]);
        }
    }

    return operation;
}

int main()
{

    string s = "";
    int operation = minimumDeletions(s);
    cout << operation;
    return 0;
}